package ir.mvbdx.mywallet.model.entity;import lombok.Data;import org.springframework.data.annotation.CreatedBy;import org.springframework.data.annotation.CreatedDate;import org.springframework.data.annotation.LastModifiedBy;import org.springframework.data.annotation.LastModifiedDate;import org.springframework.data.jpa.domain.support.AuditingEntityListener;import javax.persistence.*;import java.util.Date;@MappedSuperclass@EntityListeners({AuditingEntityListener.class})@Datapublic class BaseEntity {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    protected Long id;    @CreatedBy    @Column(updatable = false)    protected String createdBy;    @CreatedDate    @Column(updatable = false)    @Temporal(TemporalType.TIMESTAMP)    protected Date createdDate = new Date();    @LastModifiedBy    protected String lastModifiedBy;    @LastModifiedDate    @Temporal(TemporalType.TIMESTAMP)    protected Date lastModifiedDate;    protected boolean deleted = Boolean.FALSE;}